$function getImgList() {
    var str = html.match(/(var IMAGE_LIST_URL = \[.*?\];)/);
    eval(str[1]);
    var list = IMAGE_LIST_URL;
     var piclist = new Array([list.length])
    for (var i = 0; i < list.length; i++) {
        piclist[i] = list[i].url;
    }
    return piclist;
}

